import React, { useState, useEffect } from 'react';
import '../styles/HomePage.css';

const HomePage = () => {
  const [homeData, setHomeData] = useState({});

  useEffect(() => {
    fetch('/api/home')
      .then(response => response.json())
      .then(data => setHomeData(data))
      .catch(error => console.error('Error fetching home data:', error));
  }, []);

  return (
    <div className="home-page">
      {/* Intro Video Section */}
      <video className="intro-video" autoPlay muted loop>
        <source src="/assets/videos/intro-video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div className="overlay">
        <div className="container text-center py-5">
          <img src="/assets/images/logo.png" alt="EPF Logo" className="epf-logo" />
          <h1>{homeData.title || 'Welcome to Empire Partner Foundation'}</h1>
          <p>{homeData.description || 'Driving innovation and technology to solve Africa\'s problems.'}</p>
        </div>
      </div>

      {/* Content Section with Cards */}
      <div className="content-section">
        <div className="card-container">
          <div className="card">
            <img src="/assets/images/technology.png" alt="Technology" />
            <h3>Technology Innovation</h3>
            <p>Solving problems using technology to foster progress across the continent.</p>
          </div>
          <div className="card">
            <img src="/assets/images/youth.png" alt="Youth Empowerment" />
            <h3>Youth Empowerment</h3>
            <p>Empowering Africa's youth through skills development and opportunities.</p>
          </div>
          <div className="card">
            <img src="/assets/images/entrepreneurship.png" alt="Entrepreneurship" />
            <h3>Entrepreneurship</h3>
            <p>Supporting entrepreneurs to create sustainable solutions to Africa's challenges.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
