import React, { useState, useEffect } from 'react';
import '../styles/HomePage.css';

const HomePage = () => {
  const [homeData, setHomeData] = useState({});

  useEffect(() => {
    const fetchHomeData = async () => {
      try {
        const response = await fetch('/api/home');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setHomeData(data);
      } catch (error) {
        console.error('Error fetching home data:', error);
      }
    };

    fetchHomeData();
  }, []);

  return (
    <div className="home-page">
      <header className="hero-section">
        <video className="intro-video" autoPlay muted loop>
          <source src="/assets/videos/intro-video.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <div className="overlay">
          <div className="container text-center py-5">
            <h1 className="hero-title">{homeData.title || 'Empire Partner Foundation'}</h1>
            <p className="hero-description">{homeData.description || 'Driving innovation and technology to solve Africa\'s problems.'}</p>
            <a className="btn btn-primary btn-lg" href="#contact">Get in Touch</a>
          </div>
        </div>
      </header>

      <nav id="mainNav" className="navbar navbar-expand-lg navbar-light fixed-top shadow-sm">
        <div className="container">
          <a className="navbar-brand" href="#home">Empire Partner Foundation</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarResponsive" aria-controls="navbarResponsive" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarResponsive">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item"><a className="nav-link active" href="#home">Home</a></li>
              <li className="nav-item"><a className="nav-link" href="#services">Services</a></li>
              <li className="nav-item"><a className="nav-link" href="#about">About</a></li>
              <li className="nav-item"><a className="nav-link" href="#contact">Contact</a></li>
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default HomePage;
